import React from "react";
import {Helmet} from "react-helmet";
import emailjs from "emailjs-com";
import ShowMoreText from "react-show-more-text";
import { Card, CardHeader, CardBody, CardFooter } from 'react-simple-card';
import Slider from "react-slick";
import ShowMore from 'react-show-more-button';
import { Link } from 'react-router-dom';

function Shoptalk2(props) {


  function sendEmail(e) {
    e.preventDefault();

emailjs.sendForm('service_1zfatsb', 'template_aak2bzj', e.target, 'user_3TcS4pRKMgpo7fArsKmSf')
    .then((result) => {
        // console.log(result.text);
        // document.write('test case');
        alert('Thank You For Download The PDF.Please Check Your Mail,PDF Of Our Article Is Sent To Your Mail.')

    }, (error) => {
        console.log(error.text);
    });
    e.target.reset();
    
}


  return (

    <div className="postphoto">
          <Helmet>
                <style>{'body { background-color: #f2f3f5; }'}</style>
          </Helmet>

          <div class="container">
            <div class="row"style={{marginTop:"2%"}}>

                  <p style={{fontSize:"30px",fontWeight:"900",textAlign:"center"}}>Shoptalk 2022 - Solve Product Launch Delays <br/>Through Collaboration With Retail Vendors</p>
            
                  <p style={{marginTop: "-12px",textAlign:"center"}}>Did you know that delay in your product launch can cost your business 15- 35% Net Present Value? In a fast-paced world where time is money, launching the right products at the right time is crucial to maintain the flow for retailersand to stay ahead of competition.</p>
                
                 <img src="../../assets/images/Blog Cover2.gif" alt="" />

                 <i style={{fontWeight:"600",marginTop:"2%"}}>Things We Look Forward To..</i>

              <div style={{paddingLeft:"7px"}}>
                 <ul>
                     <li>Consequences of product launch delays and how to avoid them.</li>
                     <li>Relevant sessions from ShopTalk 2022 cherry picked for you.</li>
                     <li>Necessity of obtaining customer loyalty during the Post-Covid era.</li>
                     <li>Networking and collaboration opportunities with the right vendor.</li>
                 </ul>
                 </div>

              <div style={{backgroundColor:"white"}}>
                <div class="row">
                <div class="col-md-6">
                <div style={{marginTop:"4%",marginBottom:"2%"}}>
                  <p style={{textAlign:"center",color:"blue"}}>Download The PDF For Continue Reading...</p>

                  <div class="text-center">
                    <div class="" style={{overflow: "hidden", position:"relative", backgroundImage:"inherit", backgroundPosition:"inherit", backgroundSize: "inherit", backgroundRepeat: "inherit", backgroundAttachment:"inherit", backgroundOrigin:"inherit", backgroundClip: "inherit", backgroundColor: "white", maxHeight: "80px"}}>
                      <div id="anchor697948554" style={{height:"1px", position:"absolute", top:"-35px"}}></div>
                      <form>
                        <div class="mx-auto">
                        <div className="col-8 form-group mx-auto">
                    <input type="text" className="form-control" placeholder="Name" name="name"required/>
                </div>
                <div className="col-8 form-group pt-2 mx-auto">
                    <input type="text" className="form-control" placeholder="Company Name" name="company_name"required/>
                </div>
                <div className="col-8 form-group pt-2 mx-auto">
                    <input type="email" className="form-control" placeholder="Email" name="email"required/>
                </div>
                <div className="col-8 pt-3  mx-auto">
                  <div class="text-center">
                  <input type="submit" id="button-19" className="btn btn-info" value="SUBMIT"></input>
                  </div>
                </div>
                            </div>
                       </form>
                            <div style={{background: "inherit", position: "absolute", bottom: "0px", right: "0px", left: "0px", opacity: "0.93", height: "79px", display: "block"}}>
                              </div>
                              <div class="" style={{position: "absolute", bottom: "0px", left: "0px", right: "0px", display: "flex", justifyContent: "center", alignItems: "center", padding: "15px 0px", opacity: "1", zIndex: "initial"}}>
                                <button class="" style={{background: "black", color: "white", padding: "10px 15px", bordeRadius: "10px", width: "40%", maxWidth: "250px", cursor: "pointer"}}>Show More</button>
                                </div>
                             </div>
                </div>
              </div>
              </div>
              <div class="col-md-6">

                <div class="text-center"style={{marginTop: "8%"}}>
                  
                  <button id="button-37">Book an appointment <span>&#8594;</span></button>
                </div>

                <p style={{textAlign:"center"}}>Or Make a Call:</p>
                <p style={{textAlign:"center",fontSize:"22px",fontWeight:"900",marginTop:"-3%"}}>+1 (415) 799-7876</p>

              </div>

          </div>
          </div>

              </div>
        </div>
        
        <br/>
<br/>

    </div>

  );
  
}


export default Shoptalk2;
